mapper.select:
SELECT *
FROM WORK
WHERE PROJECT_NO = #{pno}
AND MEMBER_NO = #{memberNo}
AND STATUS != 'PL승인완료'
AND WORK_TYPE != '개인'

SELECT *
FROM WORK
WHERE PROJECT_NO = #{pno}
AND MEMBER_NO = #{memberNo}
AND STATUS NOT IN ('PL검토중', 'PL승인완료')
AND WORK_TYPE != '개인'

SELECT COUNT(*)
FROM ISSUE
WHERE PROJECT_NO = #{pno}
